import React, { useState, useEffect } from 'react';
import {useHistory } from "react-router-dom";
import '../css/NavBar.css';
import {
    Link
  } from "react-router-dom";
  import LinkedInIcon from '@material-ui/icons/LinkedIn';
  import GitHubIcon from '@material-ui/icons/GitHub';

  const NavBar = () => {
    const history = useHistory();
    const [value, setValue] = useState(window.location.pathname);

    const handleNavigation = (path) => {
        if (path === '/resume' && window.innerWidth <= 767) {
            history.push('/'); // Redirect to Home on small screens when Resume is clicked
        } else {
            history.push(path);
        }
        setValue(window.location.pathname);
    };

    return (
        <nav>
            <img className='logo' src="/images/kostas.jpg" alt="fg" />
            <h3 className='mt-4'>Konstantinos Mazarakis</h3>
            <p className='mb-4'>FULLSTACK DEVELOPER</p>
            <Link to="/" className='nv hover-underline-animation' onClick={() => handleNavigation('/')} id={value === "/" ? "active" : ""}>Home</Link>
            <Link to="/about" className='nv hover-underline-animation' onClick={() => handleNavigation('/about')} id={value === "/about" ? "active" : ""}>About</Link>
            <Link to="/projects" className='nv hover-underline-animation' onClick={() => handleNavigation('/projects')} id={value === "/projects" ? "active" : ""}>Projects</Link>
            <Link to="/skills" className='nv hover-underline-animation' onClick={() => handleNavigation('/skills')} id={value === "/skills" ? "active" : ""}>Skills</Link>
            <Link to="/resume" className='nv hover-underline-animation' onClick={() => handleNavigation('/resume')} id={value === "/resume" ? "active" : ""}>Resume</Link>
            <Link to="/certifications" className='nv hover-underline-animation' onClick={() => handleNavigation('/certifications')} id={value === "/certifications" ? "active" : ""}>Certifications</Link>
            <Link to="/contact" className='nv hover-underline-animation' onClick={() => handleNavigation('/contact')} id={value === "/contract" ? "active" : ""}>Contact</Link>
            <p className='mt-5'>
                <span className='me-2'><a href="https://www.linkedin.com/in/konstantinos-mazarakis/"><LinkedInIcon fontSize='large' color="primary"/></a></span>
                <span><a className='style-delete' href="https://github.com/KonstantinosMazarakis"><GitHubIcon fontSize='large'/></a></span>
            </p>
        </nav>
    );
};

export default NavBar;
